import React, { useState } from 'react';
import CityWeather from './CityWeather';
import SearchCity from './SearchCity';

const CityWeathers = () => {
    const [cityWeather, setCityWeather] = useState(null);
    const [isLoading, setLoading] = useState(false);
    const [error, setError] = useState(false);

    function getCityWeather(city) {
        setLoading(true);
        setError(false);
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${process.env.REACT_APP_OPENWEATHERMAP_API_KEY}`)
            .then(res => res.json())
            .then(res => {
                if(res.cod === "404") {
                    setError(true);
                } else {
                    console.log(res);
                    const oldState = cityWeather;
                    const newState = []
                    setCityWeather((prevState) => [...prevState, res])
                }
            })
            .catch(err => setError(true))
            .finally(() => setLoading(false))
    }

    console.log(cityWeather);

    return (
        <div className="container">
            <h2>Weather</h2>
            <SearchCity handleClick={getCityWeather} />
            {isLoading && (
                <p>Loading..</p>
            )}
            {error && (
                <p>Error! City weather information couldn't fetch. Try again.</p>
            )}
            {!error && !isLoading && cityWeather ? (
                cityWeather.map((weather) => (
                    <CityWeather
                    cityName={weather.name}
                    countryName={weather.sys.country}
                    weatherMain={weather.weather[0].main}
                    weatherDescription={weather.weather[0].description}
                    minTemp={weather.main.temp_min}
                    maxTemp={weather.main.temp_max}
                    lat={weather.coord.lat}
                    lon={weather.coord.lon} />
                ))

            ) : (
                    <p>Please write the city name!</p>
                )}
        </div>
    );
}

export default CityWeathers;
